sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord() || newValue === "" || newValue ==oldValue) {
      return;
   }

   //Type appropriate comment here, and begin script below
	var ga = new GlideAjax("GetEmailAddress");
	ga.addParam("sysparm_name","getEmail");
	ga.addParam("sysparm_userId",newValue);
	ga.getXML(populateEmail);

	function populateEmail(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue("u_requested_for_email",answer);
	}


}
